    private void registerbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registerbuttonActionPerformed
        // Change password action
        String user = username.getText().trim();
        char[] passChars = passwordfield.getPassword();
        String newPass = new String(passChars);
        char[] confirmPassChars = confirmpassword.getPassword();
        String confirmPass = new String(confirmPassChars);

        if (newPass.isEmpty() || confirmPass.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter and confirm your new password.");
            return;
        }

        if (!newPass.equals(confirmPass)) {
            JOptionPane.showMessageDialog(this, "Passwords do not match.");
            return;
        }

        if (newPass.length() < 8) {
            JOptionPane.showMessageDialog(this, "Password must be at least 8 characters long.");
            return;
        }

        try {
            String hashedPass = config.dbConnect.hashPassword(newPass);
            config.dbConnect db = new config.dbConnect();
            String updateSql = "UPDATE users SET Password = '" + hashedPass + "' WHERE Username = '" + user + "'";
            int result = db.InsertData(updateSql);
            if (result == 1) {
                JOptionPane.showMessageDialog(this, "Password changed successfully!");
                // Optionally disable password fields again
                passwordfield.setEnabled(false);
                confirmpassword.setEnabled(false);
            } else {
                JOptionPane.showMessageDialog(this, "Failed to change password. Please try again.");
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }
    }//GEN-LAST:event_registerbuttonActionPerformed

