    private void checkbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkbuttonActionPerformed
        // Check username and secret answer
        String user = username.getText().trim();
        String secretAns = secretanswer.getText().trim();

        if (user.isEmpty() || secretAns.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter both username and secret answer.");
            return;
        }

        try {
            config.dbConnect db = new config.dbConnect();
            String query = "SELECT * FROM users WHERE Username = '" + user + "' AND secretanswer = '" + secretAns + "'";
            java.sql.ResultSet rs = db.getData(query);
            if (rs.next()) {
                JOptionPane.showMessageDialog(this, "Verification successful! You can now change your password.");
                passwordfield.setEnabled(true);
                confirmpassword.setEnabled(true);
            } else {
                JOptionPane.showMessageDialog(this, "Invalid username or secret answer.");
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }
    }//GEN-LAST:event_checkbuttonActionPerformed

