    private void registerbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registerbuttonActionPerformed
        // Handle user registration
        String fname = firstname.getText().trim();
        String lname = lastname.getText().trim();
        String user = username.getText().trim();
        String pass = passwordfield.getText();
        String mail = email.getText().trim();
        String phone = phonenumber.getText().trim();
        String addr = address.getText().trim();
        String secretAns = secretanswer.getText().trim();

        // Validate required fields
        if (fname.isEmpty() || lname.isEmpty() || user.isEmpty() || pass.isEmpty() || mail.isEmpty() || secretAns.isEmpty()) {
            javax.swing.JOptionPane.showMessageDialog(this, "Please fill in all required fields (First Name, Last Name, Username, Password, Email, Secret Answer).");
            return;
        }

        // Validate password length
        if (pass.length() < 8) {
            javax.swing.JOptionPane.showMessageDialog(this, "Password must be at least 8 characters long.");
            return;
        }

        // Validate phone number contains only digits (if not empty)
        if (!phone.isEmpty() && !phone.matches("\\d+")) {
            javax.swing.JOptionPane.showMessageDialog(this, "Phone number should contain only digits.");
            return;
        }

        try {
            // Hash the password
            String hashedPass = config.dbConnect.hashPassword(pass);

            // Check if username or email already exists
            config.dbConnect db = new config.dbConnect();
            String checkUserSql = "SELECT * FROM users WHERE username = '" + user + "' OR email = '" + mail + "'";
            java.sql.ResultSet rs = db.getData(checkUserSql);
            if (rs.next()) {
                javax.swing.JOptionPane.showMessageDialog(this, "Username or Email already exists. Please choose another.");
                return;
            }

            // Insert new user including secret answer
            String insertSql = "INSERT INTO users (FirstName, LastName, Username, Password, Email, Phone, Address, Status, Role, image, secretanswer) VALUES ('"
                    + fname + "', '" + lname + "', '" + user + "', '" + hashedPass + "', '" + mail + "', '" + phone + "', '" + addr + "', 'Pending', 'User', 'src/images/Background.png', '" + secretAns + "')";
            int result = db.InsertData(insertSql);
            if (result == 1) {
                javax.swing.JOptionPane.showMessageDialog(this, "Registration successful!");
              
            } else {
                javax.swing.JOptionPane.showMessageDialog(this, "Registration failed. Please try again.");
            }
        } catch (Exception e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }
    }//GEN-LAST:event_registerbuttonActionPerformed

